// Calculate daily HP penalties based on character activity
const characters = $('Fetch Active Characters').all().map(item => item.json);
const overdueHabits = $('Check Overdue Habits').all().map(item => item.json);
const overdueTasks = $('Check Overdue Tasks').all().map(item => item.json);

const penalties = characters.map(char => {
  // Find overdue data for this character
  const habitData = overdueHabits.find(h => h.character_id === char.id) || { overdue_habits: 0 };
  const taskData = overdueTasks.find(t => t.character_id === char.id) || { overdue_tasks: 0 };

  // Calculate penalties
  let hpPenalty = 0;
  let reason = [];

  // Penalty for overdue habits (2 HP per overdue habit, max 20)
  if (habitData.overdue_habits > 0) {
    const habitPenalty = Math.min(habitData.overdue_habits * 2, 20);
    hpPenalty += habitPenalty;
    reason.push(`${habitData.overdue_habits} overdue habits (-${habitPenalty} HP)`);
  }

  // Penalty for overdue tasks (5 HP per overdue task, max 25)
  if (taskData.overdue_tasks > 0) {
    const taskPenalty = Math.min(taskData.overdue_tasks * 5, 25);
    hpPenalty += taskPenalty;
    reason.push(`${taskData.overdue_tasks} overdue tasks (-${taskPenalty} HP)`);
  }

  // Penalty for negative coins (overdraft penalty)
  if (char.coins < 0) {
    const overdraftPenalty = Math.min(Math.abs(char.coins) * 0.1, 15);
    hpPenalty += Math.floor(overdraftPenalty);
    reason.push(`Overdraft penalty (-${Math.floor(overdraftPenalty)} HP)`);
  }

  // Daily maintenance bonus for active players (level 10+)
  let hpBonus = 0;
  if (char.level >= 10 && hpPenalty === 0) {
    hpBonus = 5;
    reason.push(`Daily wellness bonus (+${hpBonus} HP)`);
  }

  return {
    characterId: char.id,
    userId: char.user_id,
    username: char.username,
    currentHp: char.hp,
    hpPenalty: hpPenalty,
    hpBonus: hpBonus,
    netHpChange: hpBonus - hpPenalty,
    newHp: Math.max(0, Math.min(100, char.hp + hpBonus - hpPenalty)),
    reason: reason.join(', ') || 'No changes',
    shouldUpdate: (hpPenalty > 0 || hpBonus > 0)
  };
});

return penalties.filter(p => p.shouldUpdate).map(p => ({ json: p }));

// Generate daily events for active characters
const characters = $('Fetch Active Characters').all().map(item => item.json);
const eventTemplates = $('Fetch Available Events').all().map(item => item.json);

const dailyEvents = [];

characters.forEach(character => {
  // Each character gets 1-3 daily events
  const numEvents = Math.floor(Math.random() * 3) + 1;

  for (let i = 0; i < numEvents; i++) {
    const template = eventTemplates[Math.floor(Math.random() * eventTemplates.length)];

    if (template) {
      dailyEvents.push({
        characterId: character.id,
        templateId: template.id,
        eventId: `daily_${character.id}_${template.id}_${Date.now()}_${i}`,
        name: template.name,
        description: template.description,
        coins: template.coins,
        hp: template.hp,
        xp: template.xp
      });
    }
  }
});

return dailyEvents.map(event => ({ json: event }));
