// Process and format database health analysis results
const tableStats = $input.first().json;
const indexStats = $input.all()[1].json;
const dbOverview = $input.all()[2].json;

// Create comprehensive health report
const healthReport = {
  timestamp: new Date().toISOString(),
  analysis_type: 'comprehensive_health_check',
  database_overview: {},
  table_analysis: {
    total_tables: tableStats.length,
    tables: tableStats,
    health_alerts: []
  },
  index_analysis: {
    total_indexes: indexStats.length,
    indexes: indexStats,
    unused_indexes: indexStats.filter(idx => idx.usage_category === 'UNUSED'),
    low_usage_indexes: indexStats.filter(idx => idx.usage_category === 'LOW USAGE')
  },
  recommendations: []
};

// Process database overview
dbOverview.forEach(metric => {
  healthReport.database_overview[metric.metric.toLowerCase().replace(/\s+/g, '_')] = {
    value: metric.value,
    description: metric.description
  };
});

// Analyze table health and generate alerts
tableStats.forEach(table => {
  // Check for high dead tuple percentage
  if (table.dead_tuple_percentage > 20) {
    healthReport.table_analysis.health_alerts.push({
      severity: 'warning',
      table: table.tablename,
      issue: 'High dead tuple percentage',
      value: `${table.dead_tuple_percentage}%`,
      recommendation: 'Consider running VACUUM on this table'
    });
  }

  // Check for tables that haven't been analyzed recently
  if (!table.last_analyze && !table.last_autoanalyze) {
    healthReport.table_analysis.health_alerts.push({
      severity: 'info',
      table: table.tablename,
      issue: 'Never analyzed',
      recommendation: 'Run ANALYZE to update table statistics'
    });
  }
});

// Generate general recommendations
if (healthReport.index_analysis.unused_indexes.length > 0) {
  healthReport.recommendations.push({
    type: 'performance',
    priority: 'medium',
    description: `Found ${healthReport.index_analysis.unused_indexes.length} unused indexes`,
    action: 'Consider dropping unused indexes to save storage space'
  });
}

if (healthReport.table_analysis.health_alerts.filter(alert => alert.severity === 'warning').length > 0) {
  healthReport.recommendations.push({
    type: 'maintenance',
    priority: 'high',
    description: 'Tables with high dead tuple percentage detected',
    action: 'Schedule VACUUM operations for affected tables'
  });
}

return { json: healthReport };
